// src/pages/AddNote.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { axiosJWT, BASE_URL } from "../utils";

const AddNote = () => {
    const [judul, setJudul] = useState("");
    const [deskripsi, setDeskripsi] = useState("");
    const navigate = useNavigate();

    const saveNote = async (e) => {
        e.preventDefault();
        try {
            await axiosJWT.post(`${BASE_URL}/note`, {
                judul,
                deskripsi
            });
            navigate("/"); // pastikan ini sesuai dengan route dashboard kamu
        } catch (error) {
            console.log("Gagal menambahkan catatan:", error);
        }
    };

    return (
        <div className="columns mt-5 is-centered">
            <div className="column is-half"> 
                <h1 className="title">Tambah Catatan</h1>
                <form onSubmit={saveNote}>
                    <div className="field">
                        <label className="label">Judul</label>
                        <div className="control">
                            <input
                                type="text"
                                className="input"
                                value={judul}
                                onChange={(e) => setJudul(e.target.value)}
                                placeholder="Judul"
                            />
                        </div>
                    </div>
                    <div className="field">
                        <label className="label">Deskripsi</label>
                        <div className="control">
                            <textarea
                                className="textarea"
                                value={deskripsi}
                                onChange={(e) => setDeskripsi(e.target.value)}
                                placeholder="Deskripsi"
                            />
                        </div>
                    </div>
                    <div className="field">
                        <button type="submit" className="button is-success">Save</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddNote;
