import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode } from "jwt-decode";
import { useNavigate, Link } from 'react-router-dom';

const BASE_URL = "http://localhost:5000";

const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [user, setUser] = useState([]);
    const [note, setNote] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const initialize = async () => {
            await refreshToken();
            await getUser();
            await getNote();
        };
        initialize();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get(`${BASE_URL}/token`);
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate("/");
            }
        }
    };

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get(`${BASE_URL}/token`);
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUser = async () => {
        try {
            const response = await axiosJWT.get(`${BASE_URL}/user`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setUser(response.data);
        } catch (error) {
            console.error("Gagal mengambil data user:", error);
        }
    };

    const getNote = async () => {
        try {
            const response = await axios.get(`${BASE_URL}/note`);
            setNote(response.data);
        } catch (error) {
            console.error("Gagal mengambil catatan:", error);
        }
    };

    const deleteNote = async (id) => {
        try {
            await axios.delete(`${BASE_URL}/note/${id}`);
            getNote(); // refresh data setelah delete
        } catch (error) {
            console.error("Gagal menghapus catatan:", error);
        }
    };

    return (
        <div className="columns mt-5 is-centered">
            <div className="column is-half">
                <h1 className="title">Selamat datang, {name}</h1>
                <Link to={`add`} className="button is-success mb-4">Add Note</Link>
                <table className="table is-striped is-fullwidth">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Judul</th>
                            <th>Deskripsi</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {note.map((note, index) => (
                            <tr key={note.id}>
                                <td>{index + 1}</td>
                                <td>{note.judul}</td>
                                <td>{note.deskripsi}</td>
                                <td>
                                    <Link to={`edit/${note.id}`} className="button is-small is-info mr-2">Edit</Link>
                                    <button onClick={() => deleteNote(note.id)} className="button is-small is-danger">Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Dashboard;
