import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

const BASE_URL = "http://localhost:5000";

const EditNote = () => {
    const [judul, setJudul] = useState("");
    const [deskripsi, setDeskripsi] = useState("");
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const navigate = useNavigate();
    const { id } = useParams();

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get(`${BASE_URL}/token`);
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    useEffect(() => {
        const init = async () => {
            await refreshToken();
            await getNoteById();
        };
        init();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get(`${BASE_URL}/token`);
            setToken(response.data.accessToken);
            const decoded = jwtDecode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate("/");
            }
        }
    };

    const getNoteById = async () => {
        try {
            const response = await axiosJWT.get(`${BASE_URL}/note/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setJudul(response.data.judul);
            setDeskripsi(response.data.deskripsi);
        } catch (error) {
            console.log("Gagal mengambil data catatan:", error);
        }
    };

    const updateNote = async (e) => {
        e.preventDefault();
        try {
            await axiosJWT.patch(`${BASE_URL}/note/${id}`, {
                judul,
                deskripsi
            }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            navigate("/");
        } catch (error) {
            console.log("Gagal mengupdate catatan:", error);
        }
    };

    return (
        <div className="columns mt-5 is-centered">
            <div className="column is-half">
                <h1 className="title">Edit Note</h1>
                <form onSubmit={updateNote}>
                    <div className="field">
                        <label className="label">Judul</label>
                        <div className="control">
                            <input
                                type="text"
                                className="input"
                                value={judul}
                                onChange={(e) => setJudul(e.target.value)}
                                placeholder="Judul"
                            />
                        </div>
                    </div>
                    <div className="field">
                        <label className="label">Deskripsi</label>
                        <div className="control">
                            <textarea
                                className="textarea"
                                value={deskripsi}
                                onChange={(e) => setDeskripsi(e.target.value)}
                                placeholder="Deskripsi"
                            ></textarea>
                        </div>
                    </div>
                    <div className="field">
                        <button type="submit" className="button is-success">
                            Update
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EditNote;
